package main

import (
    "strings"
    "strconv"
)

func max_product(s string) int64 {
    start := 0
    var max int64 = 0

    for start + 12 < len(s) {
        if tmp, err := strconv.ParseInt(s[start:start + 13], 10, 64); err == nil {
            var tmp_product int64 = 1
            for tmp > 0 {
                tmp_product *= tmp % 10
                tmp /= 10
            }

            if tmp_product > max {
                max = tmp_product
            }
        }
        start++
    }

    return max
}

func main(){
    var result int64 = 0
    s := ""
    array_s := strings.Split(s, "0")

    for i := range array_s {
        if len(array_s[i]) > 12 && max_product(array_s[i]) > result {
            result = max_product(array_s[i])
        }
    }

    println(result)
}
